import "./Login.css";
import { useState } from "react";
import { Link } from 'react-router-dom';
import { FaEye, FaEyeSlash } from 'react-icons/fa';
import instance from "../../api";

const Login = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [login, setLogin] = useState({
    email: '',
    password: ''
  })

  const handleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleInputChange = () => {
    setLogin({
      ...login, [e.target.name]: e.target.value
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await instance.post('/api/users/login', login)
      setLogin({
        email: '',
        password: ''
      })
      toast.success('Sign up successful! Redirecting to login page...', {
        autoClose: 2500,
      });
      navigate('/');
    } catch (error) {
      // console.error(error.response.data)
      const errorMessage = error.response.data.message || 'Sign up failed. Please try again.'
      toast.error(errorMessage);
    }
  }

  return (
    <>
      <div className='login-wrapper'>
        <div className="login-wrapper-logo">
          <Link to="/"><img src="logo.png" alt="" width="140rem" height="70rem" /></Link>
          <h1>Welcome to MarketEase!</h1>
          <p>Your trusted place to sell and buy freely</p>
        </div>
        <div className="login-body">
          <h1>Log In</h1>
          <form className='login-form' onSubmit={handleSubmit}>

            <input
              type="email"
              id='email'
              name="email"
              placeholder="Email"
              required
            />
            <div className="password-field">
              <input
                type={showPassword ? "text" : "password"}
                id='password'
                name="password"
                placeholder="Password"
                required
              />
              <span className="password-visibility-toggle" onClick={handleShowPassword}>
                {showPassword ? <FaEye /> : <FaEyeSlash />}
              </span>
            </div>

            <button type="submit">Log In</button>
            <p>Don't have an account? <Link to='/signup'>Sign up</Link></p>
          </form>
        </div>
      </div>
    </>

  );
}

export default Login;
